<template>
  <div class="tk-avatar" @click="onClick">
    <el-tooltip v-if="!readonly" class="item" effect="light" placement="bottom-start" v-model="showMetaInput" manual>
      <div class="tk-avatar-placeholder">
        <img v-if="avatar" :src="avatar" alt="">
      </div>
      <template v-slot:content>
        <div class="meta-input-group">
          <el-input placeholder="请输入内容" v-model="metaData.nick">
            <template slot="prepend">昵称</template>
          </el-input>
          <el-input placeholder="请输入内容" v-model="metaData.mail">
            <template slot="prepend">邮箱</template>
          </el-input>
          <el-input placeholder="请输入内容" v-model="metaData.site">
            <template slot="prepend">网址</template>
          </el-input>
        </div>
      </template>
    </el-tooltip>
    <el-tooltip v-if="readonly" class="item" effect="dark" placement="bottom-start" :content="nick || '匿名'">
      <div class="tk-avatar-placeholder">
        <img v-if="avatar" :src="avatar" alt="">
      </div>
    </el-tooltip>
  </div>
</template>

<script>
import md5 from 'blueimp-md5'

export default {
  props: {
    nick: String,
    mail: String,
    site: String,
    readonly: Boolean
  },
  data () {
    return {
      metaData: {
        nick: '',
        mail: '',
        site: ''
      },
      avatar: '',
      showMetaInput: false
    }
  },
  methods: {
    initMeta () {
      if (localStorage.getItem('twikoo')) {
        this.metaData = JSON.parse(localStorage.getItem('twikoo'))
      }
      this.updateMeta()
    },
    updateMeta () {
      localStorage.setItem('twikoo', JSON.stringify(this.metaData))
      this.$emit('update', this.metaData)
    },
    updateAvatar () {
      if (this.mail) {
        this.avatar = `https://gravatar.loli.net/avatar/${md5(this.mail)}`
      } else {
        this.avatar = ''
      }
    },
    onClick () {
      this.$emit('click')
      if (this.readonly) {
        this.site && window.open(this.site)
      } else {
        this.showMetaInput = !this.showMetaInput
      }
    }
  },
  watch: {
    nick (newVal) { this.metaData.nick = newVal },
    mail (newVal) {
      this.metaData.mail = newVal
      this.updateAvatar()
    },
    site (newVal) { this.metaData.site = newVal }
  },
  mounted () {
    if (this.readonly) {
      this.updateAvatar()
    } else {
      this.initMeta()
    }
  }
}
</script>

